import React, { useState } from 'react';
import { View, Text, TextInput, StyleSheet, Alert, TouchableOpacity, Image } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

export default function StudentDashboard({ navigation, route }) {
    const [pin, setPin] = useState('');
    const user = route.params?.user || { username: 'TÉ™lÉ™bÉ™' };

    const joinGame = () => {
        if (!pin) return Alert.alert('XÉ™ta', 'ZÉ™hmÉ™t olmasa PIN kodu daxil edin');
        navigation.navigate('GameLobby', { pin, username: user.username, isHost: false });
    };

    const handleLogout = async () => {
        await AsyncStorage.removeItem('user');
        navigation.replace('Login');
    };

    return (
        <LinearGradient
            colors={['#4a148c', '#7b1fa2']}
            style={styles.container}
        >
            <View style={styles.topHeader}>
                <TouchableOpacity onPress={() => navigation.navigate('Profile', { user })} style={styles.headerBtn}>
                    <Text style={styles.headerBtnText}>ðŸ‘¤ Profil</Text>
                </TouchableOpacity>
                <TouchableOpacity onPress={handleLogout} style={styles.headerBtn}>
                    <Text style={styles.headerBtnText}>ðŸšª Ã‡Ä±xÄ±ÅŸ</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.card}>
                <Text style={styles.header}>CanlÄ± Oyun</Text>
                <Text style={styles.subHeader}>MÃ¼É™llimin verdiyi PIN kodu daxil et</Text>

                <TextInput
                    style={styles.pinInput}
                    placeholder="000000"
                    placeholderTextColor="#ccc"
                    value={pin}
                    onChangeText={setPin}
                    keyboardType="numeric"
                    maxLength={6}
                />

                <TouchableOpacity style={styles.enterBtn} onPress={joinGame}>
                    <Text style={styles.enterBtnText}>Oyuna Gir ðŸš€</Text>
                </TouchableOpacity>
            </View>
        </LinearGradient>
    );
}

const styles = StyleSheet.create({
    container: { flex: 1, justifyContent: 'center', padding: 20 },
    card: { backgroundColor: 'white', borderRadius: 20, padding: 30, alignItems: 'center', elevation: 10, shadowColor: '#000', shadowOffset: { width: 0, height: 5 }, shadowOpacity: 0.3, shadowRadius: 10 },
    header: { fontSize: 32, fontWeight: 'bold', color: '#4a148c', marginBottom: 10 },
    subHeader: { fontSize: 16, color: 'gray', marginBottom: 30, textAlign: 'center' },
    pinInput: { fontSize: 40, fontWeight: 'bold', letterSpacing: 5, color: '#333', borderBottomWidth: 3, borderBottomColor: '#4a148c', width: '80%', textAlign: 'center', marginBottom: 30, paddingBottom: 10 },
    enterBtn: { backgroundColor: '#4a148c', paddingVertical: 15, paddingHorizontal: 40, borderRadius: 30, elevation: 5 },
    enterBtnText: { color: 'white', fontSize: 20, fontWeight: 'bold' },

    topHeader: { flexDirection: 'row', justifyContent: 'space-between', paddingHorizontal: 10, marginBottom: 50, marginTop: 10 },
    headerBtn: { backgroundColor: 'rgba(255,255,255,0.2)', paddingVertical: 10, paddingHorizontal: 20, borderRadius: 20 },
    headerBtnText: { color: 'white', fontWeight: 'bold', fontSize: 14 }
});
