import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import api from '../services/api';

// Premium Gradient Colors
const GRADIENTS = [
    ['#8e44ad', '#9b59b6'], // Purple
    ['#2980b9', '#3498db'], // Blue
    ['#27ae60', '#2ecc71'], // Green
    ['#d35400', '#e67e22'], // Orange
    ['#c0392b', '#e74c3c'], // Red
    ['#16a085', '#1abc9c'], // Teal
    ['#2c3e50', '#34495e'], // Dark Blue
];

export default function SubjectsScreen({ navigation }) {
    const [subjects, setSubjects] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchSubjects();
    }, []);

    const fetchSubjects = async () => {
        try {
            const res = await api.get('/quizzes/subjects');
            setSubjects(res.data);
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const selectSubject = (subjectName) => {
        navigation.navigate('Grades', { subject: { name: subjectName } });
    };

    return (
        <View style={styles.container}>
            <Text style={styles.header}>Fənn Seçimi</Text>

            {loading ? <ActivityIndicator size="large" color="#4a148c" /> : (
                <FlatList
                    data={subjects}
                    numColumns={2}
                    keyExtractor={(item) => item}
                    contentContainerStyle={{ paddingBottom: 20 }}
                    renderItem={({ item, index }) => (
                        <TouchableOpacity
                            style={styles.cardContainer}
                            onPress={() => selectSubject(item)}
                        >
                            <LinearGradient
                                colors={GRADIENTS[index % GRADIENTS.length]}
                                style={styles.card}
                                start={{ x: 0, y: 0 }}
                                end={{ x: 1, y: 1 }}
                            >
                                <Text style={styles.cardText}>{item}</Text>
                                <Text style={styles.cardSubText}>Daxil Ol ➜</Text>
                            </LinearGradient>
                        </TouchableOpacity>
                    )}
                />
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    container: { flex: 1, padding: 15, backgroundColor: '#f0f2f5' },
    header: { fontSize: 28, fontWeight: 'bold', marginBottom: 20, textAlign: 'center', color: '#4a148c', marginTop: 10 },
    cardContainer: { flex: 1, margin: 8, height: 130, borderRadius: 15, elevation: 6, shadowColor: '#000', shadowOffset: { width: 0, height: 4 }, shadowOpacity: 0.3, shadowRadius: 5 },
    card: { flex: 1, justifyContent: 'center', alignItems: 'center', borderRadius: 15, padding: 10 },
    cardText: { color: 'white', fontSize: 20, fontWeight: 'bold', textAlign: 'center', marginBottom: 5 },
    cardSubText: { color: 'rgba(255,255,255,0.8)', fontSize: 12, fontWeight: '600' }
});
