import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, TouchableOpacity, ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import AsyncStorage from '@react-native-async-storage/async-storage';
import api from '../services/api';

export default function TeacherDashboard({ navigation, route }) {
    const [quizzes, setQuizzes] = useState([]);
    const [loading, setLoading] = useState(true);
    const user = route.params?.user;

    useEffect(() => {
        const unsubscribe = navigation.addListener('focus', () => {
            fetchQuizzes();
        });
        return unsubscribe;
    }, [navigation]);

    const fetchQuizzes = async () => {
        try {
            const res = await api.get('/quizzes');
            // Filter queries created by this teacher? 
            // Ideally backend filters, but for now show all or filter if ID available
            // Assuming backend returns all, we can display all for now or filter if we had IDs.
            setQuizzes(res.data);
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const startLiveGame = (quizId) => {
        navigation.navigate('GameLobby', { quizId, isHost: true });
    };

    const handleLogout = async () => {
        await AsyncStorage.removeItem('user');
        navigation.replace('Login');
    };

    return (
        <LinearGradient colors={['#4a148c', '#7b1fa2']} style={styles.container}>
            <View style={styles.headerRow}>
                <Text style={styles.welcomeText}>Xoş gəldiniz, {user?.username || 'Müəllim'}</Text>
                <TouchableOpacity onPress={handleLogout} style={styles.logoutBtn}>
                    <Text style={styles.logoutText}>Çıxış</Text>
                </TouchableOpacity>
            </View>

            <TouchableOpacity
                style={styles.createBtn}
                onPress={() => navigation.navigate('CreateQuiz', { user })}
            >
                <Text style={styles.createBtnText}>+ Yeni İmtahan Yarat</Text>
            </TouchableOpacity>

            <Text style={styles.sectionTitle}>İmtahanlarınız</Text>

            {loading ? <ActivityIndicator size="large" color="white" /> : (
                <FlatList
                    data={quizzes}
                    keyExtractor={(item) => item._id}
                    renderItem={({ item }) => (
                        <View style={styles.card}>
                            <View style={styles.cardHeader}>
                                <Text style={styles.cardTitle}>{item.title}</Text>
                                <View style={[styles.badge, item.type === 'game' ? styles.badgeGame : styles.badgeExam]}>
                                    <Text style={styles.badgeText}>{item.type === 'game' ? 'Oyun' : 'İmtahan'}</Text>
                                </View>
                            </View>
                            <Text style={styles.cardInfo}>{item.description}</Text>
                            <Text style={styles.cardSubInfo}>{item.subject} • {item.grade}. Sinif • {item.questions.length} Sual</Text>

                            {item.type === 'game' && (
                                <TouchableOpacity
                                    style={styles.playBtn}
                                    onPress={() => startLiveGame(item._id)}
                                >
                                    <Text style={styles.playBtnText}>▶ Canlı Başlat</Text>
                                </TouchableOpacity>
                            )}
                        </View>
                    )}
                    contentContainerStyle={{ paddingBottom: 20 }}
                />
            )}
        </LinearGradient>
    );
}

const styles = StyleSheet.create({
    container: { flex: 1, padding: 20, paddingTop: 40 },
    headerRow: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 },
    welcomeText: { fontSize: 20, color: 'white', fontWeight: 'bold' },
    logoutBtn: { backgroundColor: 'rgba(255,255,255,0.2)', padding: 8, borderRadius: 8 },
    logoutText: { color: 'white', fontWeight: 'bold' },
    createBtn: { backgroundColor: 'white', padding: 15, borderRadius: 12, alignItems: 'center', marginBottom: 25, elevation: 5 },
    createBtnText: { color: '#4a148c', fontSize: 18, fontWeight: 'bold' },
    sectionTitle: { fontSize: 18, color: 'rgba(255,255,255,0.8)', marginBottom: 10, fontWeight: 'bold' },
    card: { backgroundColor: 'white', borderRadius: 15, padding: 15, marginBottom: 15, elevation: 3 },
    cardHeader: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', marginBottom: 5 },
    cardTitle: { fontSize: 18, fontWeight: 'bold', color: '#333', flex: 1 },
    badge: { paddingHorizontal: 8, paddingVertical: 4, borderRadius: 5 },
    badgeGame: { backgroundColor: '#e1bee7' },
    badgeExam: { backgroundColor: '#c5cae9' },
    badgeText: { fontSize: 12, fontWeight: 'bold', color: '#4a148c' },
    cardInfo: { color: '#666', marginBottom: 5 },
    cardSubInfo: { color: '#999', fontSize: 12, marginBottom: 10 },
    playBtn: { backgroundColor: '#ff9800', padding: 10, borderRadius: 8, alignItems: 'center', marginTop: 5 },
    playBtnText: { color: 'white', fontWeight: 'bold' }
});
