import React, { useState } from 'react';
import { View, Text, TextInput, Button, ScrollView, StyleSheet, Alert, TouchableOpacity, Image } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import api from '../services/api';

export default function CreateQuizScreen({ navigation, route }) {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [subject, setSubject] = useState('Riyaziyyat');
    const [grade, setGrade] = useState('5');
    const [type, setType] = useState('exam'); // 'exam' or 'game'
    const [questions, setQuestions] = useState([]);

    const [currentQ, setCurrentQ] = useState({
        text: '',
        questionType: 'choice', // 'choice' or 'classic'
        options: ['', '', '', ''],
        correctIndex: 0,
        correctAnswer: '',
        imageUrl: null
    });

    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            allowsEditing: true,
            quality: 0.5,
            base64: true
        });
        if (!result.canceled) {
            setCurrentQ({ ...currentQ, imageUrl: `data:image/jpeg;base64,${result.assets[0].base64}` });
        }
    };

    const addQuestion = () => {
        if (!currentQ.text) return Alert.alert('X…ôta', 'Sual m…ôtni daxil edin');
        setQuestions([...questions, currentQ]);
        setCurrentQ({
            text: '',
            questionType: 'choice',
            options: ['', '', '', ''],
            correctIndex: 0,
            correctAnswer: '',
            imageUrl: null
        });
    };

    const saveQuiz = async () => {
        try {
            const user = route.params?.user;
            const teacherId = user?._id || user?.id;

            if (!teacherId) {
                Alert.alert('X…ôta', 'M√º…ôllim m…ôlumatƒ± tapƒ±lmadƒ±. Yenid…ôn giri≈ü edin.');
                return;
            }

            await api.post('/quizzes', { title, description, subject, grade, type, questions, teacherId });
            Alert.alert('Uƒüurlu', 'Quiz Yadda Saxlandƒ±!');
            navigation.goBack();
        } catch (err) {
            Alert.alert('X…ôta', 'Quiz yadda saxlanƒ±lmadƒ±');
        }
    };

    return (
        <ScrollView style={styles.container}>
            <Text style={styles.header}>Quiz Yarat</Text>

            <Text style={styles.label}>Quiz Tipi:</Text>
            <View style={styles.row}>
                <TouchableOpacity
                    style={[styles.typeBtn, type === 'exam' && styles.typeBtnActive]}
                    onPress={() => setType('exam')}
                >
                    <Text style={[styles.typeText, type === 'exam' && styles.typeTextActive]}>üìù Sƒ±nav (D…ôrs)</Text>
                </TouchableOpacity>
                <TouchableOpacity
                    style={[styles.typeBtn, type === 'game' && styles.typeBtnActive]}
                    onPress={() => setType('game')}
                >
                    <Text style={[styles.typeText, type === 'game' && styles.typeTextActive]}>üéÆ Oyun (Canlƒ±)</Text>
                </TouchableOpacity>
            </View>
            <Text style={styles.helperText}>
                {type === 'exam' ? 'T…ôl…ôb…ôl…ôr bu quizi ist…ôdikl…ôri vaxt h…ôll ed…ô bil…ôc…ôkl…ôr.' : 'Bu quiz yalnƒ±z siz Canlƒ± Oyun ba≈ülatdƒ±qda oynanƒ±lacaq.'}
            </Text>

            <Text style={styles.label}>Ba≈ülƒ±q:</Text>
            <TextInput placeholder="Quiz Ba≈ülƒ±ƒüƒ±" value={title} onChangeText={setTitle} style={styles.input} />

            <Text style={styles.label}>A√ßƒ±qlama:</Text>
            <TextInput placeholder="A√ßƒ±qlama" value={description} onChangeText={setDescription} style={styles.input} />

            <Text style={styles.label}>F…ônn:</Text>
            <TextInput placeholder="F…ônn (M…ôs: Riyaziyyat)" value={subject} onChangeText={setSubject} style={styles.input} />

            <Text style={styles.label}>Sinif (1-11):</Text>
            <TextInput placeholder="Sinif (M…ôs: 5)" value={grade} onChangeText={setGrade} keyboardType="numeric" style={styles.input} />

            <Text style={styles.subHeader}>Sual ∆èlav…ô Et (C…ômi: {questions.length})</Text>

            <View style={styles.typeRow}>
                <TouchableOpacity
                    style={[styles.smallTypeBtn, currentQ.questionType === 'choice' && styles.typeBtnActive]}
                    onPress={() => setCurrentQ({ ...currentQ, questionType: 'choice' })}
                >
                    <Text style={[styles.smallTypeText, currentQ.questionType === 'choice' && styles.typeTextActive]}>≈ûƒ±qlƒ±</Text>
                </TouchableOpacity>
                <TouchableOpacity
                    style={[styles.smallTypeBtn, currentQ.questionType === 'classic' && styles.typeBtnActive]}
                    onPress={() => setCurrentQ({ ...currentQ, questionType: 'classic' })}
                >
                    <Text style={[styles.smallTypeText, currentQ.questionType === 'classic' && styles.typeTextActive]}>Klassik</Text>
                </TouchableOpacity>
            </View>

            <TouchableOpacity style={styles.imagePickerBtn} onPress={pickImage}>
                <Text style={styles.imagePickerText}>{currentQ.imageUrl ? "‚úÖ ≈û…ôkil Se√ßildi" : "üì∑ ≈û…ôkil ∆èlav…ô Et"}</Text>
            </TouchableOpacity>
            {currentQ.imageUrl && <Image source={{ uri: currentQ.imageUrl }} style={styles.previewImage} />}

            <View style={styles.mathHelper}>
                <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, text: currentQ.text + '¬≤' })}><Text>x¬≤</Text></TouchableOpacity>
                <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, text: currentQ.text + '¬≥' })}><Text>x¬≥</Text></TouchableOpacity>
                <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, text: currentQ.text + '‚àö' })}><Text>‚àö</Text></TouchableOpacity>
                <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, text: currentQ.text + 'œÄ' })}><Text>œÄ</Text></TouchableOpacity>
                <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, text: currentQ.text + '‚ÇÅ' })}><Text>x‚ÇÅ</Text></TouchableOpacity>
                <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, text: currentQ.text + '‚ÇÇ' })}><Text>x‚ÇÇ</Text></TouchableOpacity>
            </View>
            <TextInput
                placeholder="Sual M…ôtni (Mat. d√ºsturlar: x¬≤ + y = 10)"
                value={currentQ.text}
                onChangeText={(t) => setCurrentQ({ ...currentQ, text: t })}
                style={[styles.input, { height: 80 }]}
                multiline
            />

            {currentQ.questionType === 'choice' ? (
                <>
                    {currentQ.options.map((opt, idx) => (
                        <TextInput
                            key={idx}
                            placeholder={`Variant ${idx + 1}`}
                            value={opt}
                            onChangeText={(t) => {
                                const newOpts = [...currentQ.options];
                                newOpts[idx] = t;
                                setCurrentQ({ ...currentQ, options: newOpts });
                            }}
                            style={styles.inputSmall}
                        />
                    ))}
                    <Text style={styles.label}>Doƒüru Cavab ƒ∞ndeksi (0-3):</Text>
                    <TextInput
                        value={String(currentQ.correctIndex)}
                        onChangeText={(t) => setCurrentQ({ ...currentQ, correctIndex: parseInt(t) || 0 })}
                        keyboardType="numeric"
                        style={styles.inputSmall}
                    />
                </>
            ) : (
                <>
                    <Text style={styles.label}>Doƒüru Cavab (M…ôtn):</Text>
                    <View style={styles.mathHelper}>
                        <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, correctAnswer: currentQ.correctAnswer + '¬≤' })}><Text>x¬≤</Text></TouchableOpacity>
                        <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, correctAnswer: currentQ.correctAnswer + '¬≥' })}><Text>x¬≥</Text></TouchableOpacity>
                        <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, correctAnswer: currentQ.correctAnswer + '‚ÇÅ' })}><Text>x‚ÇÅ</Text></TouchableOpacity>
                        <TouchableOpacity style={styles.mathBtn} onPress={() => setCurrentQ({ ...currentQ, correctAnswer: currentQ.correctAnswer + '‚ÇÇ' })}><Text>x‚ÇÇ</Text></TouchableOpacity>
                    </View>
                    <TextInput
                        placeholder="M…ôs…ôl…ôn: x = 15"
                        value={currentQ.correctAnswer}
                        onChangeText={(t) => setCurrentQ({ ...currentQ, correctAnswer: t })}
                        style={styles.input}
                    />
                </>
            )}

            <Button title="Sualƒ± ∆èlav…ô Et" onPress={addQuestion} />

            <View style={{ marginTop: 20, marginBottom: 40 }}>
                <Text style={{ textAlign: 'center', marginBottom: 10 }}>C…ômi Suallar: {questions.length}</Text>
                <Button title="Quizi Yadda Saxla" onPress={saveQuiz} color="#4a148c" />
            </View>
        </ScrollView>
    );
}

