import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, StyleSheet, Alert, TouchableOpacity, ActivityIndicator } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import AsyncStorage from '@react-native-async-storage/async-storage';
import api from '../services/api';

export default function AdminDashboard({ navigation, route }) {
    const [pendingTeachers, setPendingTeachers] = useState([]);
    const [loading, setLoading] = useState(true);
    const user = route.params?.user;

    useEffect(() => {
        fetchPending();
    }, []);

    const fetchPending = async () => {
        try {
            const res = await api.get('/auth/pending');
            setPendingTeachers(res.data);
        } catch (err) {
            Alert.alert('Xəta', 'Məlumat alınamadı');
        } finally {
            setLoading(false);
        }
    };

    const approveUser = async (userId) => {
        try {
            await api.post('/auth/approve', { userId });
            Alert.alert('Uğurlu', 'Müəllim təsdiqləndi');
            fetchPending(); // Refresh list
        } catch (err) {
            Alert.alert('Xəta', 'Təsdiqləmə uğursuz oldu');
        }
    };

    const handleLogout = async () => {
        await AsyncStorage.removeItem('user');
        navigation.replace('Login');
    };

    return (
        <LinearGradient colors={['#1a237e', '#283593']} style={styles.container}>
            <View style={styles.headerRow}>
                <Text style={styles.title}>Admin Paneli</Text>
                <View style={{ flexDirection: 'row' }}>
                    <TouchableOpacity onPress={() => navigation.navigate('Profile', { user })} style={[styles.logoutBtn, { marginRight: 10 }]}>
                        <Text style={styles.logoutText}>Profil</Text>
                    </TouchableOpacity>
                    <TouchableOpacity onPress={handleLogout} style={styles.logoutBtn}>
                        <Text style={styles.logoutText}>Çıxış</Text>
                    </TouchableOpacity>
                </View>
            </View>
            <Text style={styles.subtitle}>Xoş gəldiniz, {user?.username || 'Admin'}</Text>

            <View style={styles.contentContainer}>
                <Text style={styles.sectionTitle}>Təsdiq Gözləyən Müəllimlər</Text>

                {loading ? <ActivityIndicator color="#4a148c" size="large" /> : (
                    pendingTeachers.length === 0 ? (
                        <View style={styles.emptyContainer}>
                            <Text style={styles.emptyIcon}>✓</Text>
                            <Text style={styles.emptyText}>Bütün müəllimlər təsdiqlənib.</Text>
                        </View>
                    ) : (
                        <FlatList
                            data={pendingTeachers}
                            keyExtractor={(item) => item._id}
                            renderItem={({ item }) => (
                                <View style={styles.card}>
                                    <View>
                                        <Text style={styles.username}>{item.username}</Text>
                                        <Text style={styles.email}>{item.email}</Text>
                                        <Text style={styles.role}>Müəllim</Text>
                                    </View>
                                    <TouchableOpacity
                                        style={styles.approveBtn}
                                        onPress={() => approveUser(item._id)}
                                    >
                                        <Text style={styles.approveBtnText}>Təsdiqlə</Text>
                                    </TouchableOpacity>
                                </View>
                            )}
                        />
                    )
                )}
            </View>
        </LinearGradient>
    );
}

const styles = StyleSheet.create({
    container: { flex: 1, paddingTop: 50 },
    headerRow: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', paddingHorizontal: 20, marginBottom: 10 },
    title: { fontSize: 28, fontWeight: 'bold', color: 'white' },
    logoutBtn: { backgroundColor: 'rgba(255,255,255,0.2)', paddingVertical: 8, paddingHorizontal: 15, borderRadius: 8 },
    logoutText: { color: 'white', fontWeight: 'bold' },
    subtitle: { fontSize: 16, marginBottom: 30, color: 'rgba(255,255,255,0.7)', paddingHorizontal: 20 },
    contentContainer: { flex: 1, backgroundColor: '#f5f5f5', borderTopLeftRadius: 30, borderTopRightRadius: 30, padding: 20 },
    sectionTitle: { fontSize: 20, fontWeight: 'bold', marginBottom: 20, color: '#333' },
    emptyContainer: { alignItems: 'center', marginTop: 50 },
    emptyIcon: { fontSize: 50, color: '#4caf50', marginBottom: 10 },
    emptyText: { fontStyle: 'italic', color: 'gray', fontSize: 16 },
    card: { flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center', padding: 20, backgroundColor: 'white', marginBottom: 15, borderRadius: 15, elevation: 4 },
    username: { fontSize: 18, fontWeight: 'bold', color: '#333' },
    email: { color: 'gray', fontSize: 14, marginBottom: 5 },
    role: { color: '#3f51b5', fontSize: 12, fontWeight: 'bold', backgroundColor: '#e8eaf6', alignSelf: 'flex-start', paddingHorizontal: 6, paddingVertical: 2, borderRadius: 4 },
    approveBtn: { backgroundColor: '#4caf50', paddingVertical: 10, paddingHorizontal: 20, borderRadius: 10, elevation: 2 },
    approveBtnText: { color: 'white', fontWeight: 'bold' }
});
