import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Alert, Button, ScrollView, Image, TextInput } from 'react-native';
import { socket } from '../services/socket';
import api from '../services/api';
import { LinearGradient } from 'expo-linear-gradient';

export default function GameScreen({ route, navigation }) {
    const { isHost, quizId, pin, mode, user } = route.params; // mode: 'solo' or undefined (live)
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [questions, setQuestions] = useState([]);
    const [timeLeft, setTimeLeft] = useState(30);
    const [score, setScore] = useState(0);
    const [isFinished, setIsFinished] = useState(false);
    const [quizTitle, setQuizTitle] = useState('');
    const [quizSubject, setQuizSubject] = useState('');
    const [classicAnswer, setClassicAnswer] = useState('');

    // Fetch quiz data
    useEffect(() => {
        console.log('GameScreen: Mounted. Params:', route.params);
        if (quizId) {
            console.log('GameScreen: Fetching quiz', quizId);
            api.get(`/quizzes/${quizId}`).then(res => {
                console.log('GameScreen: Quiz Data:', res.data);
                if (!res.data.questions || res.data.questions.length === 0) {
                    Alert.alert('X…ôta', 'Bu quizd…ô sual yoxdur!');
                }
                setQuestions(res.data.questions || []);
                setQuizTitle(res.data.title);
                setQuizSubject(res.data.subject);
            }).catch(err => {
                console.error('GameScreen: Fetch Error', err);
                Alert.alert('Error', 'Could not load quiz: ' + err.message);
            });
        } else {
            console.error('GameScreen: No quizId provided!');
        }
    }, [quizId]);

    // Timer logic (Optional for solo, strictly enforced for live)
    useEffect(() => {
        if (isFinished || questions.length === 0) return;
        const timer = setInterval(() => {
            setTimeLeft((prev) => (prev > 0 ? prev - 1 : 0));
        }, 1000);
        return () => clearInterval(timer);
    }, [currentQuestionIndex, isFinished, questions]);

    const handleAnswer = (index) => {
        if (isFinished) return;

        // SOLO MODE LOGIC
        if (mode === 'solo') {
            const currentQ = questions[currentQuestionIndex];
            if (index === currentQ.correctIndex) {
                setScore(score + 100); // 100 pts per correct answer
                Alert.alert('Doƒüru!', '∆èla! +100 xal');
            } else {
                Alert.alert('Yanlƒ±≈ü', `Doƒüru cavab: ${currentQ.options[currentQ.correctIndex]}`);
            }
            nextQuestion();
        }
        // LIVE GAME LOGIC
        else {
            if (isHost) return;
            socket.emit('submit_answer', { pin, answer: index, username: 'Me' });
            Alert.alert('Cavab G√∂nd…ôrildi', 'Dig…ôr oyun√ßular g√∂zl…ônilir...');
        }
    };

    const nextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setTimeLeft(30); // Reset timer
        } else {
            finishGame();
        }
    };

    const finishGame = async () => {
        setIsFinished(true);
        const finalUserId = user?._id || user?.id;
        if (mode === 'solo' && finalUserId) {
            // Save Score to Backend
            try {
                await api.post('/auth/add-score', {
                    userId: finalUserId,
                    score: score,
                    title: quizTitle,
                    subject: quizSubject
                });
                console.log('Score saved successfully for user:', finalUserId);
            } catch (err) {
                console.error('Failed to save score:', err);
            }
        }
    };

    const submitScore = async () => {
        // Navigation back
        navigation.goBack();
    }

    if (!questions.length) return (
        <View style={styles.center}>
            <Text>Y√ºkl…ônir...</Text>
            <Text style={{ color: 'gray', fontSize: 10, marginTop: 10 }}>Quiz ID: {quizId}</Text>
        </View>
    );

    if (isFinished) {
        return (
            <LinearGradient colors={['#4a148c', '#7b1fa2']} style={styles.container}>
                <View style={[styles.card, { alignItems: 'center' }]}>
                    <Text style={styles.finishTitle}>T…ôbrikl…ôr! üéâ</Text>
                    <Text style={styles.scoreText}>Xalƒ±nƒ±z: {score}</Text>
                    <Text style={styles.subText}>C…ômi Sual: {questions.length}</Text>
                    <TouchableOpacity style={styles.btn} onPress={submitScore}>
                        <Text style={styles.btnText}>Ana Menyuya Qayƒ±t</Text>
                    </TouchableOpacity>
                </View>
            </LinearGradient>
        );
    }

    const currentQ = questions[currentQuestionIndex];

    return (
        <LinearGradient colors={['#f5f5f5', '#e0e0e0']} style={styles.container}>
            <View style={styles.header}>
                <Text style={styles.qCount}>Sual {currentQuestionIndex + 1}/{questions.length}</Text>
                <View style={styles.timerBox}>
                    <Text style={styles.timerText}>{timeLeft}s</Text>
                </View>
            </View>

            <View style={styles.card}>
                {currentQ.imageUrl && (
                    <Image
                        source={{ uri: currentQ.imageUrl }}
                        style={styles.questionImage}
                        resizeMode="contain"
                    />
                )}
                <Text style={styles.questionText}>{currentQ.text}</Text>
            </View>

            {isHost ? (
                <View style={styles.hostControls}>
                    <Text style={styles.hostText}>Siz HOST-sunuz</Text>
                    <Button title="N√∂vb…ôti Sual" onPress={() => { /* Logic broadcast */ }} />
                </View>
            ) : (
                currentQ.questionType === 'classic' ? (
                    <View style={styles.classicContainer}>
                        <View style={styles.mathHelper}>
                            <TouchableOpacity style={styles.mathBtn} onPress={() => {
                                Alert.alert("√úst ƒ∞ndeks", "R…ôq…ôm se√ßin:",
                                    [..."0123456789"].map(n => ({
                                        text: n,
                                        onPress: () => {
                                            const map = { '0': '‚Å∞', '1': '¬π', '2': '¬≤', '3': '¬≥', '4': '‚Å¥', '5': '‚Åµ', '6': '‚Å∂', '7': '‚Å∑', '8': '‚Å∏', '9': '‚Åπ' };
                                            setClassicAnswer(prev => prev + (map[n] || n));
                                        }
                                    })).concat([{ text: "L…ôƒüv et", style: "cancel" }])
                                );
                            }}><Text>x‚Åø</Text></TouchableOpacity>
                            <TouchableOpacity style={styles.mathBtn} onPress={() => {
                                Alert.alert("Alt ƒ∞ndeks", "R…ôq…ôm se√ßin:",
                                    [..."0123456789"].map(n => ({
                                        text: n,
                                        onPress: () => {
                                            const map = { '0': '‚ÇÄ', '1': '‚ÇÅ', '2': '‚ÇÇ', '3': '‚ÇÉ', '4': '‚ÇÑ', '5': '‚ÇÖ', '6': '‚ÇÜ', '7': '‚Å∑', '8': '‚Çà', '9': '‚Çâ' };
                                            setClassicAnswer(prev => prev + (map[n] || n));
                                        }
                                    })).concat([{ text: "L…ôƒüv et", style: "cancel" }])
                                );
                            }}><Text>x‚Çô</Text></TouchableOpacity>
                            <TouchableOpacity style={styles.mathBtn} onPress={() => setClassicAnswer(classicAnswer + '‚àö')}><Text>‚àö</Text></TouchableOpacity>
                            <TouchableOpacity style={styles.mathBtn} onPress={() => setClassicAnswer(classicAnswer + 'œÄ')}><Text>œÄ</Text></TouchableOpacity>
                        </View>
                        <TextInput
                            placeholder="Cavabƒ±nƒ±zƒ± yazƒ±n..."
                            value={classicAnswer}
                            onChangeText={setClassicAnswer}
                            style={styles.classicInput}
                        />
                        <TouchableOpacity
                            style={styles.submitBtn}
                            onPress={() => {
                                if (classicAnswer.toLowerCase().trim() === currentQ.correctAnswer.toLowerCase().trim()) {
                                    setScore(score + 100);
                                    Alert.alert('Doƒüru!', '∆èla! +100 xal');
                                } else {
                                    Alert.alert('Yanlƒ±≈ü', `Doƒüru cavab: ${currentQ.correctAnswer}`);
                                }
                                setClassicAnswer('');
                                nextQuestion();
                            }}
                        >
                            <Text style={styles.btnText}>Cavabƒ± G√∂nd…ôr</Text>
                        </TouchableOpacity>
                    </View>
                ) : (
                    <View style={styles.optionsGrid}>
                        {currentQ.options.map((opt, idx) => (
                            <TouchableOpacity
                                key={idx}
                                style={[styles.optionBtn, { backgroundColor: getColor(idx), borderColor: getColor(idx) }]}
                                onPress={() => handleAnswer(idx)}
                            >
                                <Text style={styles.optionText}>{opt}</Text>
                            </TouchableOpacity>
                        ))}
                    </View>
                )
            )}
        </LinearGradient>
    );
}

